{
    "patient": {
        "user_id": INT,  //user_id from Step 2
        "ehr": {
            "personal": {
                "heightFeet": INT,
                "heightInches": INT,
                "weight": INT  // lbs
            },
            "medications": [ //array can be empty if no medications
                {
                    "userMedication_id": INT,  // only required if it's an existing record carried over from Step 2. Do not include for new records
                    "comment": STRING,
                    "foreignId": STRING,
                    "frequency": STRING,
                    "isCurrentlyUsing": BOOLEAN,
                    "name": STRING,
                    "ndc": STRING
                }
            ],
            "allergies": [  //array can be empty if no allergies
                {
                    "userMedicationAllergy_id": INT,  // only required if it's an existing record carried over from Step 2. Do not include for new records
                    "description": STRING,
                    "foreign_id": INT,
                    "damConceptId": INT,
                    "damConceptIdType": INT,
                    "whenCreated": STRING, // date/time format YYYY-MM-DD HH:MM:SS
                    "active": INT //0 or 1
                },
            ],
            "medical_conditions": [ //array can be empty if no medical_conditions
                {
                    "userMedicalCondition_id": INT,  // only required if it's an existing record carried over from Step 2. Do not include for new records
                    "name": STRING,
                    "description": STRING,
                    "status": INT //0 or 1
                }
            ],
            "surgical_history": [  //array can be empty if no surgical procedures
                {
                    "userSurgicalHistory_id": INT,  // only required if it's an existing record carried over from Step 2. Do not include for new records
                    "name": STRING,
                    "procedureDate": DATE, // YYYY-MM-DD
                    "description": STRING
                }
            ],
            "attachments": [INT] //array of attachment_id's, can be empty if no attachments
        }
    },
    "payment": {
        "fee": DECIMAL,  // 0.00
        "nonce": STRING 
    },
    "modality": STRING,   // phone or video
    "sureScriptPharmacy_id": INT,  //retreived from Pharmacy Search results
    "appointment_details": {
        "when_scheduled": STRING, // use "now" for next available doctor within 2 hours; or use a date/time YYYY-MM-DD HH:MM:SS to schedule a future consult date and time. i.e "2022-08-01 17:30:00"
        "consult_time_zone": STRING, // IANA ZONE NAME from api/timezone/all
        "preferred_language": STRING, // use "en" for english, "es" for spanish, 
    },
    "state": INT, //state_id from Step 2: available_states
    "reason_for_visit": STRING,
    "prescription_refill": {
        "is_needed": BOOLEAN,
        "prescription_details": STRING
    },
    "patientPhone": STRING,  // valid phone number required, dummy phone numbers will be rejected
    "problems": {
        "chief_complaint_id": 1,  // problem_id from Step 2: available_problems
        "other_problems": [1,2,3]  // problem_id from Step 2: available_problems
    },
    "roi": "PCP" // "What would you have done if you didn't have this service?" Member selects one of the following: "PCP","Urgent Care","Emergency Room","Nothing"
}

